import logo from './logo.svg';
import React, { useState } from "react";
import './App.css';
import { eventWager } from './abi/abi';
import { token } from './abi/abi';
import Web3 from "web3";

const web3 = new Web3(Web3.givenProvider);
const contractAddress = "0x69F9fbb28815614b47B2B04150F92685187eD50d";
const tokenAddress = "0xa0d904cd8c4E983604c791024E9Faa46e8170C82";
const eventWagerContract = new web3.eth.Contract(eventWager, contractAddress);
const tokenContract = new web3.eth.Contract(token, tokenAddress);

function App() {
  // Getter hooks
  const [retrievedWager, setRetrievedWager] = useState(0);
  const [currentPot, setRetrievedCurrentPot] = useState(0);
  const [retrievedUserSide, setRetrievedUserSide] = useState(0);

  // Setter hooks
  const [newWager, setWager] = useState(0);
  const [userSide, setUserSide] = useState(0);
  const [winningSide, setWinningSide] = useState(0);

  // Getter methods
  const getWager = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const post = await eventWagerContract.methods.getWager(account).call();
    // var amnt = post / 10**18; // Convert from Wei
    const _wager = web3.utils.fromWei(post);
    setRetrievedWager(_wager);
  };

  const getCurrentPot = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const post = await eventWagerContract.methods.getPot().call();
    const _pot = web3.utils.fromWei(post);
    setRetrievedCurrentPot(_pot);
  };

  const getUserSide = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const post = await eventWagerContract.methods.getUserSide(account).call();
    setRetrievedUserSide(post);
  }

  // Setter methods
  const wager = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const _wager = web3.utils.toWei(newWager);

      // Debug
    console.log(account);
    console.log(web3.utils.fromWei(_wager));
    const bal = await tokenContract.methods.balanceOf(account).call();
    console.log(web3.utils.fromWei(bal));
    console.log(accounts);

    const gas = await eventWagerContract.methods.wager(userSide, _wager).estimateGas();
    const post = await eventWagerContract.methods.wager(userSide, _wager).send({ from: account, gas });
    getWager(t);
    getCurrentPot(t);
  };

  const endRound = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const gas = await eventWagerContract.methods.wager(winningSide).estimateGas();
    const post = await eventWagerContract.methods.wager(winningSide).send({ from: account, gas });
  };

  const beginRound = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const gas = await eventWagerContract.methods.beginRound().estimateGas();
    const post = await eventWagerContract.methods.beginRound().send({ from: account, gas });
  };

  const allowSpend = async (t) => {
    t.preventDefault();
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const gas = tokenContract.methods.approve(contractAddress, ((2^256)-1)).estimateGas();
    const post = tokenContract.methods.approve(contractAddress, ((2^256)-1)).send({ from: account, gas });
  };


  return (
    <div className="main">
      <div className="card">
        <form className="form" onSubmit={wager}>
          <label>
            Enter your wager and side:
            <br />
            <input
              className="input"
              type="text"
              name="name"
              onChange={(t) => setWager(t.target.value)}
            />
            <input
              className="input"
              type="text"
              name="side"
              onChange={(t) => setUserSide(t.target.value)}
            />
          </label>
          <button className="button" type="submit" value="Submit">
            Submit
          </button>
        </form>
        <br />
        <div class="horizontalgap">
          <button className="button" onClick={getWager} type="button">
            Your current wager: 
          </button>
            {retrievedWager}
        </div>
        <br /> <br /> <br />
        <button className="button" onClick={getCurrentPot} type="button">
          Click for current pot
        </button>
        {currentPot}
      </div>
    </div>
  );
}

export default App;
